import React, { useState } from 'react';
import axios from 'axios';
import { config } from '../config';

const QnaSection = ({ documentId }) => {
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleAskQuestion = async (e) => {
        e.preventDefault();
        if (!question.trim()) return;
        setIsLoading(true);
        setAnswer('Thinking...');
        try {
            const response = await axios.post(`${config.API_URL}/results/${documentId}/query`, { question });
            setAnswer(response.data.answer);
        } catch (error) {
            setAnswer("Sorry, I couldn't process your question.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="qna-section">
            <h4>Ask a Question about this Document</h4>
            <form onSubmit={handleAskQuestion}>
                <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="e.g., What was the patient count?"
                    disabled={isLoading}
                />
                <button type="submit" disabled={isLoading}>Ask</button>
            </form>
            {answer && <div className="answer-box"><p>{answer}</p></div>}
        </div>
    );
};


const DetailsModal = ({ document, onClose }) => {
    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <button className="modal-close" onClick={onClose}>&times;</button>
                <h2>{document.filename}</h2>
                <div className="modal-section">
                    <h3>Summary</h3>
                    <p>{document.summary}</p>
                </div>
                <div className="modal-section">
                    <h3>Extracted Entities</h3>
                    <table className="entities-table">
                        <thead>
                            <tr>
                                <th>Category</th>
                                <th>Text</th>
                                <th>Context</th>
                            </tr>
                        </thead>
                        <tbody>
                            {document.entities.map((entity, index) => (
                                <tr key={index}>
                                    <td>{entity.category}</td>
                                    <td>{entity.text}</td>
                                    <td className="context-cell">{entity.context}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className="modal-section">
                    <QnaSection documentId={document.documentId} />
                </div>
            </div>
        </div>
    );
};

export default DetailsModal;
